sources.main.scss:
$url: "https://raw.githubusercontent.com/ParkYoungWoong/overwatch-hero-selector-vanilla/master/images";

body {
    height: 100vh;
    background-image: url("#{$url}/bg.jpg");
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
}

.container {
    padding: 50px 0;
    .heros {
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        max-width: 700px;
        margin: auto; /* 기본적으로 요소가 블록요소여야하고 가로사이즈가 명시가 되어있어야한다 */
        padding: 40px 20px;
        .hero {
            width: 80px; height: 84px; background-color: #555; margin: 4px;
            border: 3px solid #fff; transform: skewX(-14deg);
            border-radius: 10px; box-sizing: border-box;
            transition: transform 0.1s, background-color 0.6s;
            overflow: hidden;
            &:hover {
                background-color: #ff9c00; transform: skewX(-14deg) scale(1.3);
                z-index: 1;
            }
            .image {
                width: 140%; height: 100%; background-position: center;
                background-size: 90px; background-repeat: no-repeat;
                transform: skewX(14deg) translateX(-16px);
            }
            @for $i from 1 through 32 {
                // .hero
                &:nth-child(#{$i}) .image {
                    background-image: url("#{$url}/hero#{$i}.png");
                }
            }
        }
    }
    .logo {
        max-width: 300px;
        margin: 0 auto;
        padding: 0 20px;
        img {
            width: 100%;
        }
    }
}
